import { readdir, readFile, writeFile, mkdir } from 'fs/promises';
import { join, dirname } from 'path';
import { fileURLToPath } from 'url';

const __dirname = dirname(fileURLToPath(import.meta.url));

async function createBackup() {
  const timestamp = new Date().toISOString().replace(/[:.]/g, '-');
  const backupDir = join(__dirname, '..', 'backups', `restore_point_${timestamp}`);

  try {
    // Create backup directory
    await mkdir(backupDir, { recursive: true });

    // Create manifest file
    const manifest = {
      timestamp,
      version: process.env.npm_package_version,
      files: []
    };

    // Function to recursively read directory
    async function processDirectory(dir, baseDir = '') {
      const entries = await readdir(dir, { withFileTypes: true });

      for (const entry of entries) {
        const fullPath = join(dir, entry.name);
        const relativePath = join(baseDir, entry.name);

        // Skip node_modules, .git, and backups directories
        if (['node_modules', '.git', 'backups', 'dist'].includes(entry.name)) {
          continue;
        }

        if (entry.isDirectory()) {
          await processDirectory(fullPath, relativePath);
        } else {
          // Read and store file content
          const content = await readFile(fullPath, 'utf-8');
          manifest.files.push({
            path: relativePath,
            content
          });
        }
      }
    }

    // Start processing from project root
    await processDirectory(join(__dirname, '..'));

    // Save manifest
    await writeFile(
      join(backupDir, 'manifest.json'),
      JSON.stringify(manifest, null, 2)
    );

    console.log(`Backup created successfully at: ${backupDir}`);
    return backupDir;
  } catch (error) {
    console.error('Error creating backup:', error);
    throw error;
  }
}

// Execute backup
createBackup().catch(console.error);

import { readFile, writeFile, mkdir } from 'fs/promises';
import { join, dirname } from 'path';
import { fileURLToPath } from 'url';

const __dirname = dirname(fileURLToPath(import.meta.url));

async function restoreFromBackup(backupId) {
  try {
    const backupDir = join(__dirname, '..', 'backups', backupId);
    const manifestPath = join(backupDir, 'manifest.json');

    // Read manifest
    const manifestContent = await readFile(manifestPath, 'utf-8');
    const manifest = JSON.parse(manifestContent);

    // Restore files
    for (const file of manifest.files) {
      const filePath = join(__dirname, '..', file.path);

      // Ensure directory exists
      await mkdir(dirname(filePath), { recursive: true });

      // Write file content
      await writeFile(filePath, file.content);
    }

    console.log(`Restore completed successfully from: ${backupId}`);
  } catch (error) {
    console.error('Error restoring from backup:', error);
    throw error;
  }
}

// Get backup ID from command line argument
const backupId = process.argv[2];
if (!backupId) {
  console.error('Please provide a backup ID');
  process.exit(1);
}

// Execute restore
restoreFromBackup(backupId).catch(console.error);

import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { Hero } from './components/Hero';
import { Services } from './components/Services';
import AIExpertise from './components/AIExpertise';
import SEOStrategy from './components/SEOStrategy';
import TechnicalPerformance from './components/TechnicalPerformance';
import ContentStrategy from './components/ContentStrategy';
import { Header } from './components/Header';
import { Footer } from './components/Footer';
import TermsOfServicePage from './pages/TermsOfService';
import AIMarketingAutomation from './pages/services/AIMarketingAutomation';
import { sections } from './constants/sections';

function HomePage() {
  const scrollToSection = (sectionId: string) => {
    const element = document.getElementById(sectionId);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <div className="min-h-screen bg-black text-white">
      <Header onNavigate={scrollToSection} />
      <main>
        <section id={sections.hero}>
          <Hero onGetStarted={() => scrollToSection(sections.services)} onBookDemo={() => scrollToSection(sections.contact)} />
        </section>

        <section id={sections.services}>
          <Services />
        </section>

        <section id={sections.contentStrategy}>
          <ContentStrategy />
          <TechnicalPerformance />
        </section>

        <section id={sections.about}>
          <AIExpertise />
        </section>

        <section id={sections.aiInnovation}>
          <SEOStrategy />
        </section>

        <section id={sections.contact}>
          <Footer onNavigate={scrollToSection} />
        </section>
      </main>
    </div>
  );
}

function App() {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<HomePage />} />
        <Route path="/terms-of-service" element={<TermsOfServicePage />} />
        <Route path="/services/ai-marketing-automation" element={<AIMarketingAutomation />} />
      </Routes>
    </Router>
  );
}

export default App;

